import React, { useState } from "react";
function Search({set}) {
    const [chara,setChara] = useState("");
    const [rel,setRel] = useState("");
    function charachange (e){
      setChara(e.target.value);
      
    }
    function relchange (e){
      setRel(e.target.value);
      
    }
    function setdata(){
      set(chara,rel);
    }




    return (
      
      <div>
          <input title = "輸入角色名稱" value = {chara} onchange = {charachange}></input>
          <select value = {rel} onchange = {relchange}>
            <option>請選擇查詢關係</option>
            <option>family</option>
            <option>friend</option>
            <option>enemy</option>
            <option>teacher</option>
            <option>lover</option>
          </select>
          <button onclick = {setdata}>搜尋</button>
      </div>
    );
  }
  
  export default Search;
